    private void jtUsernameFocusGained(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jtUsernameFocusGained
        if(jtUsername.getText().equals("Username"))
        {
            jtUsername.setText("");
        }
    }//GEN-LAST:event_jtUsernameFocusGained

    private void jtUsernameFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jtUsernameFocusLost
        if(jtUsername.getText().equals(""))
        {
            jtUsername.setText("Username");
        }
    }//GEN-LAST:event_jtUsernameFocusLost

    private void jPasswordFieldFocusGained(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jPasswordFieldFocusGained
        if(password=true)
        {
            jPasswordField.setText("");
            password=false;
        }
    }//GEN-LAST:event_jPasswordFieldFocusGained

    private void jlFondoMouseDragged(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jlFondoMouseDragged
        this.setLocation(this.getLocation().x + evt.getX() - x, this.getLocation().y + evt.getY() - y);
    }//GEN-LAST:event_jlFondoMouseDragged

    private void jlFondoMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jlFondoMousePressed
        x=evt.getX();
        y=evt.getY();
    }//GEN-LAST:event_jlFondoMousePressed

